(1, 1, '2023-02-22 17:57:53', '2023-02-22 17:57:53', '<!-- wp:paragraph -->\n<p>Welcome to WordPress. This is your first post. Edit or delete it, then start writing!</p>\n<!-- /wp:paragraph -->', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2023-02-22 17:57:53', '2023-02-22 17:57:53', '', 0, 'http://fluix-test.loc/?p=1', 0, 'post', '', 1),
(2, 1, '2023-02-22 17:57:53', '2023-02-22 17:57:53', '<!-- wp:image {\"align\":\"center\",\"id\":48,\"sizeSlug\":\"medium\",\"linkDestination\":\"none\",\"className\":\"mb-5\"} -->\n<figure class=\"wp-block-image aligncenter size-medium mb-5\"><img src=\"http://fluix-test.loc/wp-content/uploads/2023/02/1652878287_Fluix_Logo-300x87.png\" alt=\"\" class=\"wp-image-48\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>This is a single mode section of the accordion - the other blocks are collapsed after one of them is expanded.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:acf/accordion {\"name\":\"acf/accordion\",\"data\":{\"multiple\":\"0\",\"_multiple\":\"field_63f73d5e4e0cd\",\"title\":\"Focus on meaningful things, let\\u003cbr/\\u003e us automate the rest\",\"_title\":\"field_63f65a16acb3f\",\"details_0_summary\":\"Automate routine tasks\",\"_details_0_summary\":\"field_63f65af1acb41\",\"details_0_content\":\"There are always certain tasks that your team hates doing. After several years of in-depth research and customer interviews, we have determined that it\'s always the routine tasks that cause the most pain. \\r\\n\\r\\nTaskStream automates the tasks your people hate doing, so they can focus on meaningful things.\",\"_details_0_content\":\"field_63f65b0bacb42\",\"details_0_image\":62,\"_details_0_image\":\"field_63f65b3cacb43\",\"details_1_summary\":\"Sign, assign, and review\",\"_details_1_summary\":\"field_63f65af1acb41\",\"details_1_content\":\"Ain\'t any document gets stuck somewhere in a black hole. Each file follows the right path and ends up in the right place. Sign them if approved, assign them back if there are issues to think of.\",\"_details_1_content\":\"field_63f65b0bacb42\",\"details_1_image\":61,\"_details_1_image\":\"field_63f65b3cacb43\",\"details_2_summary\":\"Add comments\",\"_details_2_summary\":\"field_63f65af1acb41\",\"details_2_content\":\"A well-posed task is half the success. If you feel that a task requires several or more comments, just add them. And let your team know what to pay attention to.\",\"_details_2_content\":\"field_63f65b0bacb42\",\"details_2_image\":60,\"_details_2_image\":\"field_63f65b3cacb43\",\"details_3_summary\":\"Attach photos and drawings\",\"_details_3_summary\":\"field_63f65af1acb41\",\"details_3_content\":\"Attach any other manuals needed. That can be even a photo if you dare to show what is happening there on the field.\",\"_details_3_content\":\"field_63f65b0bacb42\",\"details_3_image\":59,\"_details_3_image\":\"field_63f65b3cacb43\",\"details_4_summary\":\"Get your actionable reports\",\"_details_4_summary\":\"field_63f65af1acb41\",\"details_4_content\":\"Whatever good or not-so-good thing happened, you will always stay on top of things. Detailed reports with accurate numbers and words are in front of your eyes and in your hands.\",\"_details_4_content\":\"field_63f65b0bacb42\",\"details_4_image\":58,\"_details_4_image\":\"field_63f65b3cacb43\",\"details\":5,\"_details\":\"field_63f65a3cacb40\"},\"align\":\"\",\"mode\":\"preview\"} /-->\n\n<!-- wp:separator {\"className\":\"is-style-dots\"} -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity is-style-dots\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph -->\n<p>This is a multiple mode accordion section</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:acf/accordion {\"name\":\"acf/accordion\",\"data\":{\"multiple\":\"1\",\"_multiple\":\"field_63f73d5e4e0cd\",\"title\":\"Focus on meaningful things, let\\u003cbr/\\u003e us automate the rest\",\"_title\":\"field_63f65a16acb3f\",\"details_0_summary\":\"Automate routine tasks\",\"_details_0_summary\":\"field_63f65af1acb41\",\"details_0_content\":\"There are always certain tasks that your team hates doing. After several years of in-depth research and customer interviews, we have determined that it\'s always the routine tasks that cause the most pain.\\r\\n \\r\\nTaskStream automates the tasks your people hate doing, so they can focus on meaningful things.\",\"_details_0_content\":\"field_63f65b0bacb42\",\"details_0_image\":62,\"_details_0_image\":\"field_63f65b3cacb43\",\"details_1_summary\":\"Sign, assign, and review\",\"_details_1_summary\":\"field_63f65af1acb41\",\"details_1_content\":\"Ain\'t any document gets stuck somewhere in a black hole. Each file follows the right path and ends up in the right place. Sign them if approved, assign them back if there are issues to think of.\",\"_details_1_content\":\"field_63f65b0bacb42\",\"details_1_image\":61,\"_details_1_image\":\"field_63f65b3cacb43\",\"details_2_summary\":\"Add comments\",\"_details_2_summary\":\"field_63f65af1acb41\",\"details_2_content\":\"A well-posed task is half the success. If you feel that a task requires several or more comments, just add them. And let your team know what to pay attention to.\",\"_details_2_content\":\"field_63f65b0bacb42\",\"details_2_image\":60,\"_details_2_image\":\"field_63f65b3cacb43\",\"details_3_summary\":\"Attach photos and drawings\",\"_details_3_summary\":\"field_63f65af1acb41\",\"details_3_content\":\"Attach any other manuals needed. That can be even a photo if you dare to show what is happening there on the field.\",\"_details_3_content\":\"field_63f65b0bacb42\",\"details_3_image\":59,\"_details_3_image\":\"field_63f65b3cacb43\",\"details_4_summary\":\"Get your actionable reports\",\"_details_4_summary\":\"field_63f65af1acb41\",\"details_4_content\":\"Whatever good or not-so-good thing happened, you will always stay on top of things. Detailed reports with accurate numbers and words are in front of your eyes and in your hands.\",\"_details_4_content\":\"field_63f65b0bacb42\",\"details_4_image\":58,\"_details_4_image\":\"field_63f65b3cacb43\",\"details\":5,\"_details\":\"field_63f65a3cacb40\"},\"align\":\"\",\"mode\":\"preview\"} /-->', 'Test task for the Web Developer role at Fluix', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2023-02-23 12:30:40', '2023-02-23 12:30:40', '', 0, 'http://fluix-test.loc/?page_id=2', 0, 'page', '', 0),
